2CO 13:12 Làpɛɛl mbɔr u mbwo a ndüür in'kyɛɛl.
2CO 13:13 Aŋin'kyɛɛl banswà bàmàlapà mbɔr.
2CO 13:14 Wɛy kab a ngway a Mwol Yɛsu Klistɔ, ukwen a Nzam, anà un'tüüb a Dweelà In'kyɛɛl byàkäl anà bɛ banswà.
GAL 1:1 
EOF;
if (!($bfile = preg_replace("/2CO 13:12 (.+?)GAL 1:1 /us",$jobfix,$bfile,-1,$rnum)) || $rnum!=1) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
goto RAWHIDE;




// RAWFIX BIBLE ********************
case "Holy-Bible---Yemsa---Yemsa-Bible-Ethiopic" :
if (!($bfile = preg_replace("/\x{e318}/us"," ",$bfile,-1,$rnum)) || $rnum!=10) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); } 
if (!($bfile = preg_replace("/\x{e31e}/us"," ",$bfile,-1,$rnum)) || $rnum!=4) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); } 
$bfile_saved = $bfile;
goto RAWHIDE;




// RAWFIX BIBLE ********************
case "Holy-Bible---Yombe---Yombe-Bible" :
$bigfix = <<<EOF
2CO 13:12 Luvanasana mboti beno na beno mu besi ki kinlongo.
2CO 13:13 Banlongoboso badi kuaku baluveni mboti.
2CO 13:14 Bika nlemvo wu Pfumu eto Yesu Klisto, luzolo lu Nzambi ayi kithuadi ki Pheve Yinlongo bibanga yeno beno boso.
GAL 1:1 
EOF;
if (!($bfile = preg_replace("/2CO 13:12 (.+?)GAL 1:1 /us",$bigfix,$bfile,-1,$rnum)) || $rnum!=1) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
goto RAWHIDE;




// RAWFIX BIBLE ********************
case "Holy-Bible---Zaramo---Kizalamo-Bible" :
$textfix = <<<EOF
2CO 13:12 Ilamseni wenyego kwa wenyego na mnonelo ung'alile.
2CO 13:13 Wanhu wose wa Mulungu wowalamsa mweye.
2CO 13:14 Ngekewa ya Mndewa Yesu Kilisto, noge da Mulungu, na udugu wa Loho wa Mulungu viwe namwe wose.
GAL 1:1 
EOF;
if (!($bfile = preg_replace("/2CO 13:12 (.+?)GAL 1:1 /us",$textfix,$bfile,-1,$rnum)) || $rnum!=1) { AION_ECHO("ERROR! Rawfix preg_replace(line=".__LINE__."): $rnum $file"); }
goto RAWHIDE;




// do this for all fixed bibles
RAWHIDE:
// calculate change
$len_fixed = mb_strlen($bfile);
$len_delta = $len_fixed - $len_original;
// report the RAWFIX differences!
if (is_array($rawcheck)) {
	// write out before and after files
	if (!file_put_contents(($rawf=$raw_orig .'/'.$bible.'.raw'), $bfile_saved)) { AION_ECHO("ERROR! Rawfix problem writing: $rawf"); }
	if (!file_put_contents(($rawf=$raw_fixed.'/'.$bible.'.raw'), $bfile)) { AION_ECHO("ERROR! Rawfix problem writing: $rawf"); }
	// convert to noia format to itemize changed verses!
	$noia_saved = array();
	$noia_fixed = array();
	AION_BIBLES_RAWFIX_VERSIFY($bible, $args, $type, $bfile_saved, $noia_saved);
	AION_BIBLES_RAWFIX_VERSIFY($bible, $args, $type, $bfile,       $noia_fixed);
	$vcount_org = count($noia_saved);
	$vcount_fix = count($noia_fixed);
	$vcount_del = $vcount_fix - $vcount_org;
	$average = ($vcount_del ? $len_delta / $vcount_del : $len_delta);
	// compare loop and write diff file
	foreach($noia_fixed as $ref => $verse) {
		if (empty($noia_saved[$ref])) {							$verse['RAWFIX'] = 'ADD';														$rawcheck[$ref] = $verse; }
		else if ($verse['TEXT'] != $noia_saved[$ref]['TEXT']) {	$verse['RAWFIX'] = 'MODIFY'; $verse['ORIGINAL'] = $noia_saved[$ref]['TEXT'];	$rawcheck[$ref] = $verse; }
	}
	foreach($noia_saved as $ref => $verse) {
		if (empty($noia_fixed[$ref])) {							$verse['RAWFIX'] = 'DELETE';													$rawcheck[$ref] = $verse; }
	}
	AION_unset($noia_saved); $noia_saved=NULL; unset($noia_saved);
	AION_unset($noia_fixed); $noia_fixed=NULL; unset($noia_fixed);
}

default:

}

// bye bye
AION_ECHO("WARNING! RAWFIX change for $bible,  byte-orig = $len_original,  byte-fix = $len_fixed,  byte-delta = $len_delta,  verses-orig = $vcount_org,  verses-fix = $vcount_fix,  verses-delta = $vcount_del,  average = $average");
return;
}


function AION_BIBLES_RAWFIX_VERSIFY($bible, $args, $type, $bfile, &$noia) {
	$line = FALSE;
	$fields = -1;
	while (($line===FALSE && ($line=strtok($bfile, "\r\n"))!==FALSE) || ($line = strtok( "\r\n" )) !== FALSE) {



		// DUPLICATE CODE!!!
		// ignore comments
		if (preg_match("/^[\s]*$/", $line) || preg_match("/^(#|\/\/)/", $line)) {
			if ($fields<0 && $type=='UNB' && preg_match("/^#columns\t/i",$line)) {
				if (($parsed=preg_split("/\t/", $line)) == FALSE) { AION_ECHO("ERROR! Unbound fields definition problem! ".$line); }
				if (4>($fields = count($parsed) - 1)) { AION_ECHO("ERROR! Unbound fields definition count problem! ".$line); }
				AION_unset($parsed); $parsed=NULL; unset($parsed);
			}
			continue;
		}
		// switch on file types HRT, VPL, UNB, B4U, SWD
		if ($type=='VPL' || $type=='HRT') {
			$notext = FALSE;
			if (preg_match("/\t/", $line)) { AION_ECHO("ERROR! TAB in Source! ".$line); }
			if (($space1=strpos($line,' ',0))===FALSE) { AION_ECHO("ERROR! Space1 missing! ".$line); }
			if (($colon1=strpos($line,':',$space1+1))===FALSE) { AION_ECHO("ERROR! Colon1 missing! ".$line); }
			if (($space2=strpos($line,' ',$colon1+1))===FALSE) { AION_ECHO("WARNING! Space2 missing! ".$line); $notext=TRUE; }
			$book = strtoupper(substr($line,0,$space1));
			$numb = sprintf('%03d', (int)array_search($book,array_keys($args['bibles'])));
			if (!(int)$numb) { AION_ECHO("ERROR! Bad bible book name! $book $numb $line"); }
			$chap = sprintf('%03d', (int)substr($line,$space1+1,$colon1-$space1-1));
			$vers = sprintf('%03d', (int)substr($line,$colon1+1,$space2-$colon1-1));
			$text = ($notext ? '' : trim(substr($line,$space2+1)));
		}
		else if ($type=='SWD') {
			$notext = FALSE;
			if (preg_match("/^[^A-Z]{1}/", $line)) {				continue; }
			if (preg_match("/\t/", $line)) { AION_ECHO("ERROR! TAB in Source! ".$line); }
			$matches = array();
			if (!preg_match("/^([^:]+) [0-9]+:/", $line, $matches)){AION_ECHO("ERROR! Bad SWORD verse! $line"); }
			if (empty($matches[1])) { AION_ECHO("ERROR! Bad SWORD verse book! $line"); }
			$space1 = strlen($matches[1]);
			if (($colon1=strpos($line,':',$space1+1))===FALSE) { AION_ECHO("ERROR! Colon1 missing! $line"); }
			if (($space2=strpos($line,' ',$colon1+1))===FALSE) { AION_ECHO("WARNING! Space2 missing! $line"); $notext=TRUE; }
			if(!($book = array_search($matches[1],$args['sword']))){AION_ECHO("ERROR! Bad SWORD verse book search! $line"); }
			$numb = sprintf('%03d', (int)array_search($book,array_keys($args['bibles'])));
			if (!(int)$numb) { AION_ECHO("ERROR! Bad bible book name! $book $numb $line"); }
			$chap = sprintf('%03d', (int)substr($line,$space1+1,$colon1-$space1-1));
			$vers = sprintf('%03d', (int)substr($line,$colon1+1,$space2-$colon1-1));
			$text = ($notext ? '' : trim(substr($line,$space2+1)));
			if (!(int)$chap || !(int)$vers) {						continue; }
		}
		else if ($type=='UNB') {
			if (($parsed = preg_split("/\t/", $line)) == FALSE) { AION_ECHO("ERROR! Unbound no line fields! ".$line); }
			if (count($parsed) !== $fields) { AION_ECHO("ERROR! Unbound line field count != $fields! ".$line); }
			if (empty($args['unbound'][$parsed[0]])) { AION_ECHO("ERROR! Unbound bad book name! ".$line); }
			$book = strtoupper($args['unbound'][$parsed[0]]);
			$numb = sprintf('%03d', (int)array_search($book,array_keys($args['bibles'])));
			if (!(int)$numb) { AION_ECHO("ERROR! Unbound Bad bible book name! ".$line); }
			$chap = sprintf('%03d', (int)$parsed[1]);
			$vers = sprintf('%03d', (int)$parsed[2]);
			$text = trim($parsed[$fields-1]);
			AION_unset($parsed); $parsed=NULL; unset($parsed);
			if (empty($text)) { continue; }
		}
		else if ($type=='B4U') {
			if (preg_match("/\t/", $line)) {						AION_ECHO("ERROR! TAB in Source! ".$line); }
			$matches = array();
			if (!preg_match("/^([[:alnum:]]+)\.(\d+):(\d+)\s(.*)$/u", $line, $matches)) { continue; }
			if (empty($matches[1])) {								AION_ECHO("ERROR! Bad SWORD verse book! $line"); }
			if(!($book = array_search($matches[1],$args['b4u']))){AION_ECHO("ERROR! Bad B4U verse book search! $line"); }
			$numb = sprintf('%03d', (int)array_search($book,array_keys($args['bibles'])));
			if (!(int)$numb) {										AION_ECHO("ERROR! Bad bible book name! $book $numb $line"); }
			$chap = sprintf('%03d', (int)$matches[2]);
			$vers = sprintf('%03d', (int)$matches[3]);
			$text = trim($matches[4]);
			if (!(int)$chap || !(int)$vers) {						continue; }
		}
		else {
			AION_ECHO("ERROR! RAW FIX BIBLE TYPE MISSING! $type: ".$line);
		}
		// don't need apocryphal at all or skipped books
		if ((int)$numb>66) { continue; }
		if (empty($args['database']['T_BOOKS'][$bible][array_search($book,$args['database']['T_BOOKS']['CODE'])]) ||
			$args['database']['T_BOOKS'][$bible][array_search($book,$args['database']['T_BOOKS']['CODE'])]=='NULL') { continue; }
		// duplicate verse reference!
		if (!empty($data_orig[$numb.'-'.$chap.'-'.$vers])) { AION_ECHO("ERROR! DUPLICATE ORIGINAL!\n".print_r($data_orig[$numb.'-'.$chap.'-'.$vers],TRUE)); }
		// DUPLICATE CODE!!!
		
		
		
		// conver to noia format		
		$noia[$bible.'-'.$numb.'-'.$chap.'-'.$vers] = array('BIBLE'=>$bible,'INDEX'=>$numb,'BOOK'=>$book,'CHAPTER'=>$chap,'VERSE'=>$vers,'RAWFIX'=>'','TEXT'=>$text,'ORIGINAL'=>'');
	}
}
